class Solution {
public:
    int numWays(string s) {
        // O(n) time; Math; s[i]=='0' or '1'
        int ones=0;
        vector<int> pos;
        for( int i=0; i<s.size(); i++)  ones+=s[i]-'0';
        
        if(ones==0) return ((long)s.size()-1)*(s.size()-2)/2%1000000007;
        if(ones%3!=0)   return 0;
        
        for( int i=0, counts=0; i<s.size(); i++){
            if(s[i]=='1'){
                counts++;
                if(counts==ones/3||counts==ones/3+1)
                    pos.push_back(i);
                if(counts==ones/3*2||counts==ones/3*2+1)
                    pos.push_back(i);
            }
        }
        
        return ((long)pos[1]-pos[0])*(pos[3]-pos[2])%1000000007;
    }
};